mapper.select:
select * from( select '20' kpi,a.inside, a.flowid, a.psc pscbcch, round(ratio_to_report(sum(1)) over(partition by a.flowid)*100,2) percent from t_log_own_wcdma a,t_log_manual_own t where a.flowid=t.flowid and 1=(case when t.net_type=1 then 0 else 1 end) and (a.psc!=-9999 and a.psc!=-9998) and a.flowid in #{fid}
and a.area_id=#{areaid}
group by a.flowid, a.inside, a.psc union all select '21' kpi,a.inside, a.flowid, a.bcch pscbcch, round(ratio_to_report(sum(1)) over(partition by a.flowid)*100,2) percent from t_log_own_gsm a,t_log_manual_own t where a.flowid=t.flowid and 1=(case when t.net_type=2 then 0 else 1 end) and (a.bcch!=-9999 and a.bcch!=-9998) and a.flowid in #{fid}
and a.area_id=#{areaid}
group by a.flowid,a.inside, a.bcch )
order by case when flowid =#{firstflowid} then 1 else 0 end desc,flowid,kpi,percent desc

select w.serialno serialno, w.create_date submitDatetime, a.areaname, w.break_flag breakFlag, w.test_number testNumber, w.test_address problemsAddress, t.test_endtime testendtime, w.is_verify verify from (select e.id id, e.serialno serialno, e.areaid area_id, e.break_flag break_flag, e.test_address test_address, e.create_date create_date, e.is_verify is_verify, nvl(count(t.flowid), 0) test_number from t_own_etl e, t_log_manual_own t where e.serialno = t.serialno(+)
and e.serialno =#{serialno}
and e.id=#{s_id}
group by e.id, e.serialno, e.areaid, e.break_flag, e.test_address, e.create_date, e.is_verify) w , t_log_manual_own t ,t_area_code a where
w.serialno = t.serialno(+) and t.area_id = a.areaid(+)
and w.serialno =#{serialno}
and w.id=#{s_id}
and w.area_id=#{areaid}
order by t.test_endtime desc

	     select t.flowid flowid,t.ftp_speed percent,case when t.ftp_type=1 then 4 
		when t.ftp_type=2 then 5 else 0 end as kpi from t_log_own_wcdma t
		 where  t.flowid in   
	#{fid}  
   and t.ftp_type=#{ftpType}
		 	
	    and t.area_id=#{areaid}
	
	 order by ep_time asc

    
       select 
       case when (select s.configvalue from t_sysconfig s where s.configkey='t_map_type')='baidu' then t.longitude_bmap else t.longitude_modify end longitudeModify,
       case when (select s.configvalue from t_sysconfig s where s.configkey='t_map_type')='baidu' then t.latitude_bmap else t.latitude_modify end latitudeModify,
       t.latitude,t.longitude,t.serialno,case when s.type=1 then s.name||'_A类' when s.type=2 then s.name||'_B类'
       else s.name  end scenName,t.call_phone callphone ,t.test_adress testaddress,t.failure, t.inside, t.duration,t.space,to_char(t.test_time,'yyyy-mm-dd hh24:mi:ss') testtime,
       t.isindoor,t.test_type teststatus,t.call_type calltype ,t.obstruct, t.density,t.flowid flowid,t.net_type netsystem
       ,t.test_type teststatus,t.ftp_type ftpUpdown
        from t_log_manual_own t,t_scene_code s where t.sceneid=s.sceneid(+) 
        	
	    and t.serialno =#{serialno} 
	
		
	    and t.id=#{s_id}
	
       	
	    and t.flowid =#{flowid}
	  
	  	
	    and t.area_id=#{areaid}
	
	  
	  
		  
		    
		     and (t.net_type=2 or t.net_type=3)
		    
		     
		     and t.net_type=1
		    	
		    
		   
	
	
	 
			
		    and t.test_type=#{testType}
		
	
	   and (t.net_type=1 or t.net_type=2 or t.net_type=3)  order by t.test_endtime desc

	  select r.*,q.*,v.* from (select
	   max(case when t.rscp!=-9999 then  t.rscp end) max_rscp,
	   min(case when t.rscp!=-9999 then t.rscp end ) min_rscp,
	   max(case when t.ec_no!=-9999 then t.ec_no end) max_ec_no,
	   min(case when t.ec_no!=-9999 then t.ec_no end) min_ec_no,
	   max(case when t.txpower!=-9999 then t.txpower end) max_txpower,
	   min(case when t.txpower!=-9999 then t.txpower end) min_txpower
	   from t_log_own_wcdma t where t.flowid in   
	#{fid}  
  
	   	
	    and t.area_id=#{areaid}
	
	) r,
	   ( select a.ftp_max_speed max_ftp,a.ftp_min_speed min_ftp, a.ftp_avg_speed avg_ftp 
	   from t_log_manual_own a where a.flowid in   
	#{fid}  
  
	   	
	    and a.area_id=#{areaid}
	
	) q,
	   ( select max(case when g.rxlev_Full!=-9999 then g.rxlev_Full end ) max_rxlev, 
	   min(case when g.rxlev_Full!=-9999 then g.rxlev_Full end ) min_rxlev ,
	   max(case when g.rxqual_Sub!=-9999 then g.rxqual_Sub end ) max_rxqual,
	   min(case when g.rxqual_Sub!=-9999 then g.rxqual_Sub end) min_rxqual,
	   max(case when g.c_i!=-9999 then g.c_i end ) max_ci,
	   min(case when g.c_i!=-9999 then g.c_i end) min_ci
	   from t_log_own_gsm g where g.flowid in   
	#{fid}  
  
	   	
	    and g.area_id=#{areaid}
	  
	) v

mapper.update:
update t_log_manual_own
set test_adress = #{testaddress,jdbcType=VARCHAR}
where FLOWID = #{flowid,jdbcType=VARCHAR}
